const users = global.get('users') || {}

const socketId = msg._socketId

let dirty = false

for (const userId in users) {
    const user = users[userId]
    if (user.socketIds.includes(socketId)) {
        dirty = true
        user.socketIds = user.socketIds.filter(v => v !== socketId)
        users[userId] = user
    }
}

if (dirty) {
    global.set('users', users)
    msg.payload = `Removed socket "${socketId} from users"`
} else {
    msg.payload = null
}

return msg;

